/**
 * The context for the error bars value function.
 */
export interface ErrorBarsValueArgs {
    /**
     * The point category.
     */
    category: any;
    /**
     * The point data item.
     */
    dataItem: any;
    /**
     * The point series.
     */
    series: any;
    /**
     * The point value.
     */
    value: any;
    /**
     * The point index.
     */
    index: number;
}
